<?php /**Database Table: wp_inkthemes_info,wp_table_info,lead_data, wp_field_table , wp_radio_table.**/global $wpdb,$table_prefix;	$inkthemes_table = $table_prefix . "inkthemes_info";global $inkthemes_table;if($wpdb->get_var("show tables like \"$inkthemes_table\" ") != $inkthemes_table) {$sql1 = "CREATE TABLE ".$inkthemes_table." (id mediumint(10) NOT NULL AUTO_INCREMENT,name varchar(255) NOT NULL,leadform1 TEXT NOT NULL,leadform2 TEXT NOT NULL,leadform3 TEXT NOT NULL,leadform4 TEXT NOT NULL,leadform5 TEXT NOT NULL,leadform6 TEXT NOT NULL,leadform7 TEXT NOT NULL,leadform8 TEXT NOT NULL,leadform9 TEXT NOT NULL,date	date	NOT NULL,rand_value int(10) NOT NULL,UNIQUE KEY id (id));";$wpdb->query($sql1);		}$table_info = $table_prefix . "table_info";global $table_info;	if($wpdb->get_var("show tables like \"$table_info\"") != $table_info) {$sqls = "CREATE TABLE ".$table_info." (id mediumint(10) NOT NULL AUTO_INCREMENT,lead_name varchar(255) NOT NULL,arrange int(11) NOT NULL,UNIQUE KEY id (id));";$count=1;$wpdb->query($sqls);	$wpdb->insert($table_info,array('lead_name' =>'Name', 'arrange'=>$count));$wpdb->insert($table_info,array('lead_name' =>'Email', 'arrange'=>$count+1));		$wpdb->insert($table_info,array('lead_name' =>'Number', 'arrange'=>$count+2));		$wpdb->insert($table_info,array('lead_name' =>'Message', 'arrange'=>$count+3));		$wpdb->insert($table_info,array('lead_name' =>'Label1', 'arrange'=>$count+4));		$wpdb->insert($table_info,array('lead_name' =>'Label2', 'arrange'=>$count+5));		$wpdb->insert($table_info,array('lead_name' =>'Label3', 'arrange'=>$count+6));		$wpdb->insert($table_info,array('lead_name' =>'Label4', 'arrange'=>$count+7));		$wpdb->insert($table_info,array('lead_name' =>'Label5', 'arrange'=>$count+8));		$wpdb->insert($table_info,array('lead_name' =>'Label6', 'arrange'=>$count+9));	}if(!($wpdb->query("SHOW COLUMNS FROM $table_info LIKE 'arrange'"))){$sqls = "ALTER TABLE ".$table_info." ADD (arrange int(11) NOT NULL);";$count=1;$wpdb->query($sqls);	$query =$wpdb->get_results("SELECT * FROM $table_info");foreach( $query as $res ){ $wpdb->update($table_info,array('arrange' => $res->id),array( 'id' =>$count ));$count++;}}global $wpdb,$table_prefix;$custom_table = $table_prefix . "lead_data";global $custom_table;	if($wpdb->get_var("show tables like \"$custom_table\" ") != $custom_table) {$save= "CREATE TABLE ".$custom_table." (ID mediumint(10) NOT NULL AUTO_INCREMENT,text_name varchar(255) NOT NULL,text_value varchar(255) NOT NULL,text_label varchar(255) NOT NULL,randvalue int(10) NOT NULL,PRIMARY KEY ID (ID));";$wpdb->query($save);$wpdb->insert($custom_table,array('text_name' =>'text','text_value' =>'Name','text_label' =>'Name','randvalue' =>'1',));$wpdb->insert($custom_table,array('text_name' =>'text','text_value' =>'Email','text_label' =>'Email','randvalue' =>'2',));$wpdb->insert($custom_table,array('text_name' =>'text','text_value' =>'Number','text_label' =>'Number','randvalue' =>'3',));$wpdb->insert($custom_table,array('text_name' =>'textarea','text_value' =>'Message','text_label' =>'Message','randvalue' =>'4',));}global $wpdb,$table_prefix;$radio_table = $table_prefix . "radio_table";global $radio_table;	if($wpdb->get_var("show tables like \"$radio_table\" ") != $radio_table) {$radio_save= "CREATE TABLE ".$radio_table." (ID mediumint(10) NOT NULL AUTO_INCREMENT,text_value varchar(255) NOT NULL,randvalue int(10) NOT NULL,PRIMARY KEY ID (ID));";$wpdb->query($radio_save);}$field_table = $table_prefix . "field_table";global $field_table;	if($wpdb->get_var("show tables like \"$field_table\" ") != $field_table) {$field_save= "CREATE TABLE ".$field_table." (PID mediumint(10) NOT NULL AUTO_INCREMENT,ID mediumint(10) NOT NULL,field_name varchar(255) NOT NULL,text_name varchar(255) NOT NULL,field_id varchar(255) NOT NULL,randvalue int(10) NOT NULL,arrange int(11) NOT NULL,PRIMARY KEY PID (PID));";$wpdb->query($field_save);}if(!($wpdb->query("SHOW COLUMNS FROM $field_table LIKE 'arrange'"))){$sqls = "ALTER TABLE ".$field_table." ADD (arrange int(11) NOT NULL);";$wpdb->query($sqls);$count=1;$arr=array();$arr1=array();$query1 =$wpdb->get_results("SELECT * FROM $field_table");foreach($query1 as $res1){$arr[]=$res1->PID;$arr1[]=$res1->ID;}$arrange=array();$arr[0]=1;$j=2;$arrange[0]=1;for($i=0; $i<count($arr1); $i++){	$count=1;	for($j=0; $j<=$i; $j++)	{		if($arr1[$i]==$arr1[$j])		{			$arrange[$i]=$count;			$count++;		}			}}	for($i=0; $i<count($arrange); $i++)	{		$wpdb->update($field_table, array('arrange' =>$arrange[$i]), array( 'PID' =>$arr[$i]));		}}?>